import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import Login from './Views/Login.js'
import NavBar from './Views/NavBar.js'
import AddPost from './Views/AddPost.js'
import Home from './Views/Home.js'
import Profile from './Views/Profile.js'
import Filter from './Views/Filter.js'
import { connect } from 'react-redux'
import {
  Router,
  Switch,
  Route,
  Link,
  Redirect,
  useHistory,
  useLocation,
} from "react-router-dom";
import history from './history'

class App extends Component {

  constructor(props) {
      super(props);
      this.state = {
        data: []
      };
    }

  componentDidMount() {
    fetch('https://dev-mobile-ig.herokuapp.com/users/users',{
    method: 'GET',
    })
      .then(response => response.json())
      .then(data => {
        console.log(data)
        this.setState({ data })
      });
  }

  PrivateProfile({ children, ...rest }) {
    return (
      <Route
      {...rest}
      render={({location}) =>
        this.props.isAuth ? (
          children
        ) : (
          <Redirect
            to = {{
              pathname: "/login",
              state: { from: location}
            }}
          />
        )
      }
      />
    )
  }

  PrivateLogin({ children, ...rest }) {
    return (
      <Route
      {...rest}
      render={({location}) =>
        this.props.isAuth ? (
          <Redirect
            to = {{
              pathname: "/profile",
              state: { from: location}
            }}
          />
        ) : (
          children
        )
      }
      />
    )
  }


  render(){

    const users = this.state.data.map((n,key) => {
      return <p>{n.username}</p>;
    });


    return (


      <Router history={history}>
        <div>
          <NavBar />

          <Switch>
            <Route exact path="/">
              <Home />
            </Route>

            <Route path="/addpost">
              <AddPost />
            </Route>

            <PrivateLogin path="/login">
              <Login />
            </PrivateLogin>

            <PrivateLogin path="/filter">
              <Filter />
            </PrivateLogin>

            <PrivateProfile path="/profile">
              <Profile />
            </PrivateProfile>

          </Switch>
          </div>
      </Router>

    );
  }
}




const mapStateToProps = state =>{
  return {
    isAuth: state.auth.isAuth,
    currentUser: state.user.currentUser
  }
}

export default connect(mapStateToProps)(App)
