import React, {Component} from 'react';
import { createMuiTheme, withStyles, makeStyles, ThemeProvider } from '@material-ui/core/styles';
import { purple } from '@material-ui/core/colors';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import CardActions from '@material-ui/core/CardActions';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';



const useStyles = theme => ({
  root: {
    minWidth: 275,
    height:1000,
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  username: {
    fontSize: 14,
  },
  localisation: {
  },
  pos: {
    marginBottom: 12,
  },
});




const RowPostView = (props) => {
  const classes = useStyles();
  return(
    <div>
    { props.post ? (
      <Card className={classes.root}>
  <CardContent>
  <Grid container>
  <Grid item xs={8}>
    <Typography className={classes.username} color="textSecondary" gutterBottom>
      {props.post.user}
    </Typography>
    </Grid>
    <Grid item xs={4}>
    <Typography className={classes.localisation} color="textSecondary" align="right" gutterBottom>
     123 km
    </Typography>
    </Grid>
  </Grid>
    <Typography variant="h5" component="h2">
      {props.post.titre}
    </Typography>
    <Typography className={classes.pos} color="textSecondary">
      {props.post.texte}
    </Typography>
  </CardContent>
  <CardActions>
    <Button size="small">Learn More</Button>
  </CardActions>
</Card>
    ): null
  }

  </div>
)
}

export default withStyles(useStyles) (RowPostView)
