import React, {Component} from 'react';
import { createMuiTheme, withStyles, makeStyles, ThemeProvider } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Card from '@material-ui/core/Card';



import {getAllPostsFromDb} from '../API/PostApi'




const useStyles = theme => ({
  mainPage: {
    boxShadow: "10px 10px 10px #9E9E9E",
    marginTop: 50,
    marginLeft: 50,
    marginRight: 50,
    marginBottom: 50,
    backgroundColor:green,

  },
  filterView: {
    fontStyle:bold,
    backgroundColor:blue,
  },
  actionProfileView: {
    fontStyle:bold,
    backgroundColor:red,
  }
  });

class Home extends React.Component {

  state = {
    posts:[]
  }

  constructor(props){
    super(props)
    getAllPostsFromDb().then(data => {
      const posts = data
      this.setState({posts: data})
      console.log(data)
    }).catch((error) => {
      console.log("Erreur dans le constructeur")
    })
  }

  render(){

  const {classes} = this.props

    return (

      <div >
      <Card className={classes.mainPage}>
      <Grid container>

      <Grid item className={classes.filterView}>
      </Grid>

      <Grid item className={classes.listView}>
      {this.state.posts ? (
        <Grid container >
          {this.state.posts.map(currentPost => (
            <Grid item xs={12}>
            <h1> {currentPost.titre} "   " </h1>
            </Grid>
          )
          )}
        </Grid>
      ) : "Il n'y a pas de posts"}
      </Grid>

      <Grid item className={classes.actionProfileView}>
      </Grid>
      </Grid>
        </Card>
      </div>
    )
  }
}

export default withStyles(useStyles)(Home)
